import Axios from "axios";
import {Table} from 'react-bootstrap';
import './App.css';
import React, {useState } from 'react';



  function User(){
  
   
    const [mylist,setMylist]= useState([]);
    const [mylist1,setMylist1]= useState([]);

    

    Axios.get("http://localhost:4200/author").then(
      res=> setMylist(res.data));

         Axios.get("http://localhost:4200/books").then(
          res=> setMylist1(res.data));
    return(
      <>
      <h1 align ="center"> Books and Author Name</h1>
      <hr/>

<Table striped bordered hover class="aa">
        <thead>
          <th>Book Name</th>
          <th>Price</th>
        </thead>
        <tbody>
          {mylist1.map((item,index)=>{
            return(
              <tr key={index}>
                <td>{item.bname}</td>
                <td>{item.price}</td>

              </tr>
            )
          })}
        </tbody>
      

        </Table>




        <Table striped bordered hover  class="float-left">
          <thead>
            <th>Author Name</th>
          </thead>
          
      
          <tbody>
            {mylist.map((item,index)=>{
              return(
                <tr key={index}>
                  <td>{item.aname}</td>
                  
      
                </tr>
              )
            })}
       </tbody>
      </Table>
      
     
      </>
    );
        }
export default User;
