import {Button,Form} from 'react-bootstrap';
import React,{useState} from 'react';


function Signup(){
const [name, setname] = useState('');
const [phonenumber, setphonenumber] = useState('');
const [password, setpassword] = useState('');
const [confirmpassword, setconfirmpassword] = useState('');
function handleDemo1(e)
{
  setname(e.target.value);
}

function handleDemo2(e)
{
  setphonenumber(e.target.value);
}

function handleDemo3(e)
{
  setpassword(e.target.value);
}

function handleDemo4(e)
{
  setconfirmpassword(e.target.value);
}


async function mysubmit()
{
  const data = {"name":name,"phonenumber":phonenumber,"password":password,"confirmpassword":confirmpassword};
  const config = {
    method: "POST",
    headers: {
      'Accept': 'application/json',
      'Content-Type':'application/json',
    },
    body:JSON.stringify(data)
  }
  const response = await fetch('http://localhost:4200/sign', config)
  const json = await response.json();

  console.log(json);
  if(json.length==0)
  {
    console.log("Please Fill your Details");
  }
  else if (json.length != 0)
  {
    console.log("Successfully login");
  }
}




    return(
      <>
       <form>
  <div class="form-group">
    <label for="exampleInputEmail1">Enter Your Name</label>
    <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Your UserName" onChange = {handleDemo1}/>
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">Enter Phone number</label>
    <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Enter Password"  onChange = {handleDemo2}/>
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">password</label>
    <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Enter Password"  onChange = {handleDemo3}/>
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">Confirm password</label>
    <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Enter Password"  onChange = {handleDemo4}/>
  </div>
  <br/>
  <Button type="button" onClick={mysubmit}>Submit</Button>
</form>
      </>
    );
}
export default Signup;
