import {Button,Form} from 'react-bootstrap';
import React,{useState} from 'react';
import { Axios } from 'axios';
import { useNavigate } from 'react-router-dom';

function Log(){
const [username, setUsername] = useState('');
const [password, setPassword] = useState('');
const navigate = useNavigate();

function handleDemo1(e)
{
  setUsername(e.target.value);
}

function handleDemo2(e)
{
  setPassword(e.target.value);
}
async function mysubmit()
{
  const data = {"username":username,"password":password};
  const config = {
    method: "POST",
    headers: {
      'Accept': 'application/json',
      'Content-Type':'application/json',
    },
    body:JSON.stringify(data)
  }
  const response = await fetch('http://localhost:4200/log', config)
  const json = await response.json();

  console.log(json);
  if(json.length==0)
  {
    console.log("invalid user");
  }
  else if (json[0].type=="customer")
  {
    console.log("welcon customer");
    localStorage.setItem("cname",username);
    navigate("/customer");
  }

  else if (json[0].type=="admin")
  {
    console.log("welcon admin");
    navigate("/admin");
  }
}




    return(
      <>
       <form>
  <div class="form-group">
    <label for="exampleInputEmail1">Enter Your Name</label>
    <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Your UserName" onChange = {handleDemo1}/>
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">Enter Password</label>
    <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Enter Password"  onChange = {handleDemo2}/>
  </div>
  <br/>
  <Button type="button" onClick={mysubmit}>Submit</Button>
</form>
      </>
    );
}
export default Log ;
