import {Button,Form} from 'react-bootstrap';
import React,{useState} from 'react';
import { Axios } from 'axios';
import { useNavigate } from 'react-router-dom';

function Log(){
const [name, setName] = useState('');
const [password, setPassword] = useState('');
const navigate = useNavigate();

function handleDemo1(e)
{
  setName(e.target.value);
}

function handleDemo2(e)
{
  setPassword(e.target.value);
}
async function mysubmit()
{
  const data = {"name":name,"password":password};
  const config = {
    method: "POST",
    headers: {
      'Accept': 'application/json',
      'Content-Type':'application/json',
    },
    body:JSON.stringify(data)
  }
  const response = await fetch('http://localhost:4200/log', config)
  const json = await response.json();

  console.log(json);
  if(json.length==0)
  {
    console.log("invalid user");
  }
  else if (json[0].name=="aa")
  {
    console.log("welcon customer");
    localStorage.setItem("cname",name);
    navigate("/detail");
  }
  

}



    return(
      <>
       <form>
  <div class="form-group">
    <label for="exampleInputEmail1">Enter Your Name</label>
    <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Your UserName" onChange = {handleDemo1}/>
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">Enter Password</label>
    <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Enter Password"  onChange = {handleDemo2}/>
  </div>
  <br/>
  <Button type="button" onClick={mysubmit}>Submit</Button>
</form>

<section class="vh-100" style="background-color: #508bfc;">
  <div class="container py-5 h-100">
    <div class="row d-flex justify-content-center align-items-center h-100">
      <div class="col-12 col-md-8 col-lg-6 col-xl-5">
        <div class="card shadow-2-strong" style="border-radius: 1rem;">
          <div class="card-body p-5 text-center">

            <h3 class="mb-5">Sign in</h3>

            <div class="form-outline mb-4">
              <input type="email" id="typeEmailX-2" class="form-control form-control-lg" />
              <label class="form-label" for="typeEmailX-2" onChange = {handleDemo1}>UserName</label>
            </div>

            <div class="form-outline mb-4">
              <input type="password" id="typePasswordX-2" class="form-control form-control-lg" />
              <label class="form-label" onChange = {handleDemo2} for="typePasswordX-2">Password</label>
            </div>

            <button class="btn btn-primary btn-lg btn-block" onClick={mysubmit} type="submit">Login</button>

          </div>
        </div>
      </div>
    </div>
  </div>
</section>

      </>
    );
}
export default Log ;
