import Axios from "axios";
import {Table} from 'react-bootstrap';

import React, {useState,useEffect } from 'react';
  function Listproduct(){
    const [mylist,setMylist]= useState([]);
   
     
    Axios.get("http://localhost:4200/books").then(
         res=> setMylist(res.data));
   
  console.log(mylist);
  console.log(mylist1);
    return(
      <>
      <h1 align ="center"> Books and Author Name</h1>
      <hr/>
      <Table striped bordered hover>
        <thead>
          <th>Author Name</th>
          <th>Book Name</th>
          <th>Price</th>
        </thead>
        <tbody>
          {mylist.map((item,index)=>{
            return(
              <tr key={index}>
                <td>{item.aname}</td>
              </tr>
            )
          })}
        </tbody>
        <tbody>
          {mylist1.map((item,index)=>{
            return(
              <tr key={index}>
                <td>{item.bname}</td>
                <td>{item.price}</td>
              </tr>
            )
          })}
        </tbody>
      </Table>
      </>
    );
        }
export default Listproduct;
