import {Button,Form} from 'react-bootstrap';
import React,{useState} from 'react';
import { Axios } from 'axios';
import { useNavigate } from 'react-router-dom';

function Log(){
const [name, setName] = useState('');
const [password, setPassword] = useState('');
const navigate = useNavigate();

function handleDemo1(e)
{
  setName(e.target.value);
}

function handleDemo2(e)
{
  setPassword(e.target.value);
}
async function mysubmit()
{
  const data = {"name":name,"password":password};
  const config = {
    method: "POST",
    headers: {
      'Accept': 'application/json',
      'Content-Type':'application/json',
    },
    body:JSON.stringify(data)
  }
  const response = await fetch('http://localhost:4200/log', config)
  const json = await response.json();

  console.log(json);
  if(json.length==0)
  {
    console.log("invalid user");
  }
  else if (json[0].name=="aa")
  {
    console.log("welcon customer");
    localStorage.setItem("cname",name);
    navigate("/detail");
  }
  

}



    return(
      <>



            <h3 class="mb-5">Sign in</h3>

            <div class="form-outline mb-4">
              <input type="email" id="typeEmailX-2" class="form-control form-control-lg" />
              <label class="form-label" for="typeEmailX-2" onChange = {handleDemo1}>UserName</label>
            </div>

            <div class="form-outline mb-4">
              <input type="password" id="typePasswordX-2" class="form-control form-control-lg" />
              <label class="form-label" onChange = {handleDemo2} for="typePasswordX-2">Password</label>
            </div>

            <button class="btn btn-primary btn-lg btn-block" onClick={mysubmit} type="submit">Login</button>

     

      </>
    );
}
export default Log ;
