import Axios from "axios";
import {Table} from 'react-bootstrap';

import React, {useState } from 'react';



  function Listproduct(){
    const [mylist,setMylist]= useState([]);
   
    const [mylist1,setMylist1]= useState([]);
     
    Axios.get("http://localhost:4200/author").then(
         res=> setMylist(res.data));

 

         Axios.get("http://localhost:4200/book").then(
          res=> setMylist1(res.data));
    return(
      <>
      <h1 align ="center"> Books and Author Name</h1>
      <hr/>
      <Table striped bordered hover>
        <thead>
          <th>Author Name</th>
          <th>Book Name</th>
          <th>Price</th>
        </thead>
        <tbody>
          {mylist.map((item,index)=>{
            return(
              <tr key={index}>
                <td>{item.aname}</td>
              </tr>
            )
          })}
        </tbody>
        <tbody>
          {mylist1.map((item,index)=>{
            return(
              <tr key={index}>
                <td>{item.bname}</td>
                <td>{item.bname}</td>

              </tr>
            )
          })}
        </tbody>
      
      </Table>
      </>
    );
        }
export default Listproduct;
